INSERT INTO post (id, title, content, author, rating, created_at)
VALUES
    (1, '꽃들에게 희망을 읽고', '작은 애벌레들이 서로 협력하며 나비가 되는 과정이 인상 깊었다. 나 역시 변화가 두렵지만, 책에서처럼 용기 있게 한 걸음 내딛고 싶다는 생각이 들었다.', '김지훈', 5, '2025-11-24T09:12:00.000000'),
    (2, '데미안을 읽고', '책을 읽는 동안 나 자신을 돌아보게 되었다. 선과 악의 기준이 아닌, 오직 나만의 길을 찾는 것이 진정한 성장이라는 메시지가 마음에 깊게 남았다.', '이서연', 4, '2025-11-24T10:45:00.000000'),
    (3, '어린 왕자를 읽고', '사막에서 만난 어린 왕자의 순수함이 나를 울렸다. 어른이 되면서 잃어버린 상상력과 따뜻함을 다시 떠올리게 해 준 책이다.', '박민준', 5, '2025-11-24T12:03:00.000000'),
    (4, '아몬드를 읽고', '감정을 느끼지 못하는 소년의 시선으로 세상을 바라보니, 평소 당연하게 여겼던 감정의 소중함을 다시 깨달았다.', '최하은', 4, '2025-11-24T13:27:00.000000'),
    (5, '해리 포터와 마법사의 돌을 읽고', '호그와트의 세계는 언제나 설렘을 준다. 특히 해리의 용기와 우정이 이야기를 빛나게 했고, 다시 어린 시절로 돌아간 기분이었다.', '정도윤', 5, '2025-11-24T14:16:00.000000'),
    (6, '미움받을 용기를 읽고', '타인의 시선에서 벗어나 스스로를 믿는 삶이 얼마나 중요한지 느끼게 해 준 책이다. 삶의 방향을 다시 세우는 계기가 되었다.', '한지우', 4, '2025-11-24T15:01:00.000000'),
    (7, '파친코를 읽고', '역경 속에서도 삶을 이어가는 인물들의 모습이 가슴을 울렸다. 역사적 비극 속에서도 희망을 잃지 않는 인간의 힘을 깊이 느꼈다.', '서유진', 5, '2025-11-24T15:40:00.000000'),
    (8, '모순을 읽고', '쉬운 문장 속에 담긴 깊은 감정들이 인상적이었다. 사랑과 상처가 어떻게 어우러지는지 섬세하게 묘사되어 자꾸만 마음에 남는 책.', '오시윤', 4, '2025-11-24T16:22:00.000000'),
    (9, '죽음에 관하여를 읽고', '삶과 죽음이라는 무거운 주제를 담담히 이야기해 준 책이다. 죽음이 두려움의 대상이 아니라 삶을 돌아보게 하는 거울이라는 메시지가 인상 깊었다.', '윤도현', 5, '2025-11-24T17:05:00.000000'),
    (10, '너의 췌장을 먹고 싶어를 읽고', '죽음을 앞둔 소녀와 소년의 특별한 관계가 감정을 흔들었다. 따뜻함과 아픔이 공존하는 이야기로 오래도록 여운이 남는다.', '장다혜', 5, '2025-11-24T18:11:00.000000'),
    (11, '1984를 읽고', '감시와 통제가 일상이 된 세계를 보며 자유의 소중함을 다시 깨달았다. 특히 인간의 생각까지 통제하려는 사회는 무섭고도 현실적이었다.', '김하린', 5, '2025-11-24T18:45:00.000000'),
    (12, '동물농장을 읽고', '동물들이 자유를 외쳤지만 결국 또 다른 독재로 흘러가는 과정이 씁쓸했다. 권력의 본질에 대해 깊이 생각하게 만든 책이다.', '박시우', 4, '2025-11-24T19:12:00.000000'),
    (13, '지구에서 한아뿐을 읽고', '사랑과 이별의 감정을 시적인 문장으로 담아낸 작품이었다. 읽는 내내 따뜻함과 슬픔이 함께 밀려왔다.', '최예진', 5, '2025-11-24T19:45:00.000000'),
    (14, '채식주의자를 읽고', '인간 내면의 고통이 상징적으로 표현되어 있었다. 읽는 동안 불편함을 느끼면서도 눈을 뗄 수 없었다.', '이도윤', 4, '2025-11-24T20:10:00.000000'),
    (15, '나미야 잡화점을 읽고', '과거와 현재가 편지를 통해 이어지는 구조가 흥미로웠다. 작은 친절이 누군가의 인생을 바꿀 수 있다는 메시지가 마음에 남았다.', '김하윤', 5, '2025-11-24T20:38:00.000000'),
    (16, '불편한 편의를 읽고', '편의점이라는 공간을 통해 반복되는 삶의 의미와 인간관계의 단절을 보여주었다. 주인공의 변화가 조용하지만 확실하게 다가왔다.', '정민호', 4, '2025-11-24T21:02:00.000000'),
    (17, '달러구트 꿈 백화점을 읽고', '꿈을 파는 백화점이라는 세계관이 매우 신선했다. 읽는 동안 현실의 지친 마음이 위로받는 기분이었다.', '윤서현', 5, '2025-11-24T21:30:00.000000'),
    (18, '역행자를 읽고', '삶을 뒤집는 용기가 무엇인지 진지하게 고민하게 해 주었다. 실행을 통해 변화할 수 있다는 메시지가 동기부여가 되었다.', '배정우', 4, '2025-11-24T21:55:00.000000'),
    (19, '불안의 시대를 읽고', '현대 사회의 불안이 어디에서 비롯되는지 명확하게 보여주는 책이었다. 읽고 난 뒤 마음의 구조를 조금은 이해하게 되었다.', '오가은', 4, '2025-11-24T22:20:00.000000'),
    (20, '종이 여자를 읽고', '사랑의 경계와 인간의 욕망을 독특한 방식으로 표현한 작품이었다. 현실과 상상의 경계가 흐려지는 순간들이 흥미로웠다.', '서준혁', 5, '2025-11-24T22:47:00.000000');